var parser = require('parser');

var ParserFactory = module.exports = function ParserFactory(type){
    this.type = type;
};

ParserFactory.prototype.getParser = function(fileArgument){

    if(this.type === "csv"){
        return new parser({delimiter:","}, fileArgument);
    }
    /*
    if(this.fileExtension === "strings"){
        return new parser({delimiter:",",comment:"\/*", location:this.fileLocation});
    }
    */
    if(this.type === "properties"){
        return new parser({delimiter:",",comment:"#"}, fileArgument);
    }
    return new parser({delimiter:"\n"}, fileArgument);
};
